// SPDX-License-Identifier: MIT
pragma solidity 0.8.16;

import { IBase, HarvestSwapParms } from "./IBase.sol";
import { IUniLp, SafeERC20, IERC20 } from "./IUniLp.sol";
import { IFarmable, IUniswapV2Router01 } from "./IFarmable.sol";

