pragma solidity 0.8.16;

struct Message {
    uint256 value;
    address sender;
    address client;
    uint16 chainId;
}

struct Vault {
    uint16 chainId;
    uint16 postmanId;
    bool allowed;
}

struct Request {
	address vaultAddr;
	uint256 amount;
	address allowanceTarget;
	address registry;
	bytes txData;
}

enum messageType {
    NONE,
    DEPOSIT,
    WITHDRAW,
    EMERGENCYWITHDRAW,
    HARVEST
}","keccak256":"0xe75685113a7d2bc72f97b9bf17067467c2e32ede717cc361ce40f1b77024e37e","license":"AGPL-3.0-only"},"src/interfaces/adapters/IMultichainAdapter.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.10;

interface CallProxy {
	function anyCall(
		address _to,
		bytes calldata _data,
		address _fallback,
		uint256 _toChainID,
		uint256 _flags
	) external;

	function context()
		external
		view
		returns (
			address from,
			uint256 fromChainID,
			uint256 nonce
		);

	function executor() external view returns (address executor);
}
